mapper.select:
select id as tumbupsId,tweet_id,user_id,status_flag from tumbups
where tweet_id =#{tweetId}
and user_id=#{userId}

mapper.update:
update tumbups set `status_flag` = 0
where tweet_id = #{tweetId}
and user_id =#{userId}

update tumbups set `status_flag` = 1
where tweet_id = #{tweetId}
and user_id =#{userId}
