mapper.select:
SELECT t.gmt_modified AS releaseTime, t.id AS tweetId, t.title, t.content, t.user_id AS tweetUserId, tu.user_id AS userId, u.user_name AS userName, tu.id AS tumbupsId FROM tweet t LEFT JOIN tumbups tu ON tu.tweet_id = t.id and t.status_flag=1 and (tu.status_flag=1 or tu.status_flag is null) LEFT JOIN `user` u ON u.id = tu.user_id and u.status_flag=1 where u.status_flag=1 and t.status_flag=1
and t.user_id = #{userId}
ORDER BY t.gmt_modified DESC

select t.id as tweetId,t.title,t.content,t.user_id as tweetUserId,u.user_name as tweetUserName
from tweet t left join `user` u on u.id=t.user_id
where t.id =#{tweetId} and t.status_flag=1 and u.status_flag=1 and t.user_id=#{userId}
